import { auctionConstants } from '../constants';
import { alertActions } from './';
export const auctionAction = {
    auctionInfo
}
function auctionInfo(data){
    console.log(data.currentBidAmount);
    
    return async (dispatch) => {
        try {
            dispatch(getInfo(data)); 
            dispatch(
                alertActions.success({
                    content: data.message
                }),
            );
        } catch (ex) {
            console.log(ex);
            
            dispatch(
                alertActions.error({
                    content: ex.message,
                }),
            );
        } 
    };
    function getInfo(data) {
        return {
            type: auctionConstants.AUCTION_INFO_SUCCESS,
            lastBid: data.currentBidAmount
        };
    }
}
