import { httpService } from './';
import { apiUrl } from '../config';

const apiEndpoint = apiUrl + '/search/simpleSearch';

export const searchService = {
    search,
};

async function search(keyword, category) {
    const response = await httpService.get(
        apiEndpoint + `?kw=${keyword}&cat=${category}`,
    );
    return response;
}
