import React, {Component} from 'react'
import {Button, Container, Menu, Responsive, Segment, Visibility, Icon} from "semantic-ui-react";
import {Link} from "react-router-dom";
import {routeConstants} from '../../constants';

const getWidth = () => {
  const isSSR = typeof window === 'undefined';
  return isSSR ? Responsive.onlyTablet.minWidth : window.innerWidth
};

class DesktopNavigation extends Component {
  state = {fixed: true};

  hideFixedMenu = () => this.setState({fixed: true});
  showFixedMenu = () => this.setState({fixed: true});

  render() {
    const {fixed} = this.state;
    const {user} = this.props;
    return (
      <Responsive getWidth={getWidth} minWidth={Responsive.onlyTablet.minWidth}>
        <Visibility once={false} onBottomPassed={this.showFixedMenu} onBottomPassedReverse={this.hideFixedMenu}>
          <Segment inverted={fixed} textAlign='center' style={{padding: '1em 0em', marginBottom: '3em'}} vertical>
            <Menu fixed={fixed ? 'top' : null} inverted={!fixed} pointing={!fixed} secondary={!fixed} size='large'>
              <Container>
                <Menu.Item as={Link} to={routeConstants.HOME} ><Icon name='home'/></Menu.Item>
                <Menu.Item as={Link} to={routeConstants.WATCH_LIST_VIEW} ><Icon name='heart'/></Menu.Item>
                <Menu.Item as={Link} to={routeConstants.ORDER_HISTORY} ><Icon name='clipboard list'/></Menu.Item>
                <Menu.Item  as={Link}  to={routeConstants.SHOPPING_CART}><Icon name='shopping cart'/></Menu.Item>
                <Menu.Item  as={Link}  to={routeConstants.VIEW_MY_RETURN_REQUESTS}><Icon name='clipboard outline'/></Menu.Item>
                <Menu.Item  as={Link}  to={routeConstants.FAVORITE_CATEGORY}><Icon name='star outline'/></Menu.Item>
                <Menu.Item  as={Link}  to={routeConstants.VIEW_FAQS}><Icon name='question'/></Menu.Item>
                <Menu.Item  as={Link}  to={routeConstants.MONEY_WITHDRAWAL}><Icon name='money'/></Menu.Item>
                <Menu.Item  as={Link}  to={routeConstants.CREATE_MONEY_WITHDRAWAL}><Icon name='money bill alternate outline'/></Menu.Item>

                <Menu.Item  as={Link}  to={routeConstants.VIEW_TICKETS}><Icon name='exclamation circle'/></Menu.Item>
                <Menu.Item  as={Link}  to={routeConstants.VIEW_CONTACT_US}><Icon name='phone'/></Menu.Item>
                {!user &&
                <Menu.Item position='right'>
                  <Button as={Link} to={routeConstants.LOGIN} inverted={!fixed}>Log in</Button>
                  <Button as={Link} to={routeConstants.REGISTER} inverted={!fixed} primary={fixed} style={{marginLeft: '0.5em'}}>Sign
                    Up</Button>
                </Menu.Item>
                }
                {user &&
                <React.Fragment>
                  <Menu.Item position='right'>
                    <Button as={Link} to={routeConstants.POST_NEW} inverted={!fixed} color='teal' icon='add' content='Create New Post'/>
                    <Button as={Link} to={routeConstants.POST_VIEW_All} inverted={!fixed} style={{marginLeft: '0.5em'}}>My Posts</Button>
                    <Button as={Link} to={routeConstants.LOGOUT} inverted={!fixed} color='red'
                            style={{marginLeft: '0.5em'}}>Logout</Button>
                  </Menu.Item>
                  <Menu.Item
                    as={Link}
                    to={routeConstants.PROFILE}
                  >
                    <Icon name='user' />
                    {`${user.username}`}
                  </Menu.Item>
                </React.Fragment>
                }
              </Container>
            </Menu>
          </Segment>
        </Visibility>
      </Responsive>
    )
  }
}

export default DesktopNavigation;
