import { httpService } from './';
import { apiUrl } from '../config';


export const watchListService={
    viewWatchList,removeItem,addToWatchList
}
const baseUrl= `${apiUrl}/watchList`


function viewWatchList() {
    return httpService.get(baseUrl);
}

function removeItem(postId) {
    return httpService.put(`${baseUrl}/remove/${postId}`);
}
function addToWatchList(postId){
    return httpService.put(`${baseUrl}/add/${postId}`)

}
