import { userConstants, badgesConstants } from "../constants";
import {defaultLanguage} from "../config/index"
const initialState = {
  language: defaultLanguage,
  isActive: true,
  userTransactions: [],
  userTransactionsList: [],
  userProfile: {
    phone: {},
    email: {},
    wallet: {
      amount: 0,
      locked: 0,
    },
  },
  userPublicDetails: {},
  invitations: [],
  invitationsByNumbers: [],
  auctionList: {
    won: [],
    lost: [],
    running: [],
  },
  shareableLink: "",

  verified: true,
  requestOTP: false,
  submitOTPSuccessful: false,
  soldPosts:{},
};

export function users(state = initialState, action) {
  switch (action.type) {
    case userConstants.SET_LANGUAGE:
      return {
        ...state,
        language: action.language,
      };

      case userConstants.HIDE_ALERT:
        return {
          ...state,
          isActive: action.isActive,
        };
     
    case userConstants.VIEW_AUCTION_LIST:
      return {
        ...state,
        display: action.display,
      };
    case userConstants.GET_RUNNING_AUCTIONS_REQUEST:
      return {
        ...state,
      };
    case userConstants.GET_RUNNING_AUCTIONS_SUCCESS:
      return {
        ...state,
        auctionList: {
          ...state.auctionList,
          running: action.data.auctions,
        },
      };
    case userConstants.GET_RUNNING_AUCTIONS_FAILURE:
      return {
        ...state,
      };
    case userConstants.GET_WON_AUCTIONS_REQUEST:
      return {
        ...state,
      };
    case userConstants.GET_WON_AUCTIONS_SUCCESS:
      return {
        ...state,
        auctionList: {
          ...state.auctionList,
          won: action.data.auctions,
        },
      };
    case userConstants.GET_WON_AUCTIONS_FAILURE:
      return {
        ...state,
      };
    case userConstants.GET_LOST_AUCTIONS_REQUEST:
      return {
        ...state,
      };
    case userConstants.GET_LOST_AUCTIONS_SUCCESS:
      return {
        ...state,
        auctionList: {
          ...state.auctionList,
          lost: action.data.auctions,
        },
      };
    case userConstants.GET_LOST_AUCTIONS_FAILURE:
      return {
        ...state,
      };

    case userConstants.VERIFY_EMAIL_REQUEST:
      return {
        ...state,
      };
    case userConstants.VERIFY_EMAIL_SUCCESS:
      return {
        ...state,
      };
    case userConstants.VERIFY_EMAIL_FAILURE:
      return {
        ...state,
      };
    case userConstants.SUBMIT_OTP_REQUEST:
      return {
        ...state,
      };
    case userConstants.SUBMIT_OTP_SUCCESS:
      return {
        ...state,
        verified: true,
        submitOTPSuccessful: true,
        requestOTP: false,
      };
    case userConstants.SUBMIT_OTP_FAILURE:
      return {
        ...state,
        error: action.error,
      };
    case userConstants.RESEND_OTP_REQUEST:
      return {
        ...state,
        verified: false,
      };
    case userConstants.RESEND_OTP_SUCCESS:
      return {
        ...state,
        verified: false,
      };
    case userConstants.RESEND_OTP_FAILURE:
      return {
        ...state,
        error: action.error,
      };
    //Profile
    case userConstants.VIEW_USERTRANSACTION_REQUEST:
      return {
        ...state,
      };
    case userConstants.VIEW_USERTRANSACTION_SUCCESS:
      return {
        ...state,
        userTransactions: action.userTransactions,
      };

    case userConstants.VIEW_USERTRANSACTION_FAILURE:
      return {
        ...state,
      };

    case userConstants.VIEW_USERTRANSACTIONLIST_REQUEST:
      return {
        ...state,
      };
    case userConstants.VIEW_USERTRANSACTIONLIST_SUCCESS:
      return {
        ...state,
        userTransactionsList: action.userTransactionsList,
      };

    case userConstants.VIEW_USERTRANSACTIONLIST_FAILURE:
      return {
        ...state,
      };

    case userConstants.VIEW_PROFILE_REQUEST:
      return {
        ...state,
      };
    case userConstants.VIEW_PROFILE_SUCCESS:
      return {
        ...state,
        userProfile: action.userProfile,
      };
    case userConstants.VIEW_PROFILE_FAILURE:
      return {
        ...state,
      };
    case userConstants.VIEW_INVITATIONS_REQUEST:
      return {
        ...state,
      };
    case userConstants.VIEW_INVITATIONS_SUCCESS:
      return {
        ...state,
        invitations: action.invitations,
      };
    case userConstants.VIEW_INVITATIONS_FAILURE:
      return {
        ...state,
      };

    case userConstants.SEND_INVITATIONSBYPHONENUMBER_REQUEST:
      return {
        ...state,
      };
    case userConstants.SEND_INVITATIONSBYPHONENUMBER_SUCCESS:
      return {
        ...state,
        invitationsByNumbers: action.invitationsByNumbers,
      };
    case userConstants.SEND_INVITATIONSBYPHONENUMBER_FAILURE:
      return {
        ...state,
      };

    case userConstants.RESEND_INVITATION_REQUEST:
      return {
        ...state,
      };
    case userConstants.RESEND_INVITATION_SUCCESS:
      const { invitations } = state;
      const objIndex = invitations.findIndex(
        (obj) => obj._id === action.invitation._id
      );
      invitations[objIndex] = action.invitation;

      return {
        ...state,
        invitations,
      };
    case userConstants.RESEND_INVITATION_FAILURE:
      return {
        ...state,
      };
    case userConstants.VIEW_PROFILE_USER_SETTINGS:
      return {
        ...state,
        display: action.display,
      };
    case userConstants.VIEW_PROFILE_FEEDBACK:
      return {
        ...state,
        display: action.display,
      };
    case userConstants.USERS_VIEW_PROFILE_INVITATIONS:
      return {
