        display: action.display,
      };
    case userConstants.VIEW_AVATAR:
      return {
        ...state,
        display: action.display,
      };
    case userConstants.VIEW_PROFILE_REVIEWS_AS_SELLER_REQUEST:
      return {
        ...state,
        display: action.display,
      };

    case userConstants.VIEW_PROFILE_REVIEWS_AS_SELLER_SUCCESS:
      return {
        ...state,
        reviewsAsSeller: action.reviewsAsSeller,
      };

    case userConstants.VIEW_PROFILE_REVIEWS_AS_SELLER_FAILURE:
      return {
        ...state,
        display: action.display,
      };
    case userConstants.VIEW_PROFILE_REVIEWS_AS_BUYER_REQUEST:
      return {
        ...state,
        display: action.display,
      };

    case userConstants.VIEW_PROFILE_REVIEWS_AS_BUYER_SUCCESS:
      return {
        ...state,
        reviewsAsBuyer: action.reviewsAsBuyer,
      };

    case userConstants.VIEW_PROFILE_REVIEWS_AS_BUYER_FAILURE:
      return {
        ...state,
        display: action.display,
      };
    case userConstants.VIEW_PROFILE_REVIEWS_FOR_OTHERS_REQUEST:
      return {
        ...state,
        display: action.display,
      };

    case userConstants.VIEW_PROFILE_REVIEWS_FOR_OTHERS_SUCCESS:
      return {
        ...state,
        reviewsForOthers: action.reviewsForOthers,
      };

    case userConstants.VIEW_PROFILE_REVIEWS_FOR_OTHERS_FAILURE:
      return {
        ...state,
        display: action.display,
      };
    //Change password
    case userConstants.CHANGE_PASSWORD_SHOW_MODAL:
      return {
        ...state,
        openChangePassword: action.openChangePassword,
      };
    case userConstants.CHANGE_PASSWORD_REQUEST:
      return {
        ...state,
      };
    case userConstants.CHANGE_PASSWORD_SUCCESS:
      return {
        ...state,
        openChangePassword: action.openChangePassword,
      };
    case userConstants.CHANGE_PASSWORD_FAILURE:
      return {
        ...state,
      };
    //EMail
    case userConstants.UPDATE_EMAIL_SHOW_MODAL:
      return {
        ...state,
        openUpdateEmail: action.openUpdateEmail,
      };
    case userConstants.UPDATE_EMAIL_REQUEST:
      return {
        ...state,
      };
    case userConstants.UPDATE_EMAIL_SUCCESS:
      return {
        ...state,
        openUpdateEmail: action.openUpdateEmail,
        userProfile: action.userProfile,
      };
    case userConstants.UPDATE_EMAIL_FAILURE:
      return {
        ...state,
      };
    //update phone number
    case userConstants.UPDATE_MOBILE_SHOW_MODAL:
      return {
        ...state,
        openUpdateMobile: action.openUpdateMobile,
      };
    case userConstants.UPDATE_MOBILE_REQUEST:
      return {
        ...state,
      };
    case userConstants.UPDATE_MOBILE_SUCCESS:
      return {
        ...state,
        openUpdateMobile: action.openUpdateMobile,
        userProfile: action.userProfile,
      };
    case userConstants.UPDATE_MOBILE_FAILURE:
      return {
        ...state,
      };
    //update address
    case userConstants.UPDATE_ADDRESS_SHOW_MODAL:
      return {
        ...state,
        openUpdateAddress: action.openUpdateAddress,
      };
    case userConstants.UPDATE_ADDRESS_REQUEST:
      return {
        ...state,
      };
    case userConstants.UPDATE_ADDRESS_SUCCESS:
      return {
        ...state,
        openUpdateAddress: action.openUpdateAddress,
        userProfile: action.userProfile,
      };
    case userConstants.UPDATE_ADDRESS_FAILURE:
      return {
        ...state,
      };
    case userConstants.GET_IS_USER_VERIFIED_REQUEST:
      return {
        ...state,
      };
    case userConstants.GET_IS_USER_VERIFIED_SUCCESS:
      return {
        ...state,
        isVerified: action.isVerified,
      };
    case userConstants.GET_IS_USER_VERIFIED_FAILURE:
      return {
        ...state,
      };
    // GET PUBLIC DETAILS THAT COULD BE VISIBLE TO OTHER USERS
    case userConstants.GET_PUBLIC_DETAILS_REQUEST:
      return {
        ...state,
      };
    case userConstants.GET_PUBLIC_DETAILS_SUCCESS:
      return {
        ...state,
        userPublicDetails: action.userPublicDetails,
      };
    case userConstants.GET_PUBLIC_DETAILS_FAILURE:
      return {
        ...state,
      };
    case badgesConstants.ACTIVATE_BADGE_REQUEST:
      return {
        ...state,
      };
    case badgesConstants.ACTIVATE_BADGE_SUCCESS:
      return {
        ...state,
        userProfile: action.userProfile.user,
      };
    case badgesConstants.ACTIVATE_BADGE_FAILURE:
      return {
        ...state,
      };
    case userConstants.GENERATE_SHAREABLE_LINK_REQUEST:
      return {
        ...state,
      };
    case userConstants.GENERATE_SHAREABLE_LINK_SUCCESS:
      return {
        ...state,
        shareableLink: action.shareableLink,
      };
    case userConstants.GENERATE_SHAREABLE_LINK_FAILURE:
      return {
        ...state,
      };

    case userConstants.UPDATE_ACCOUNT_STATUS_REQUEST:
      return {
        ...state,
      };
    case userConstants.UPDATE_ACCOUNT_STATUS_SUCCESS:
      return {
        ...state,
        userProfile: action.user,
      };
    case userConstants.UPDATE_ACCOUNT_STATUS_FAILURE:
      return {
        ...state,
      };

      case userConstants.GET_SOLD_POSTS_REQUEST:
        return {
          ...state,
        };
      case userConstants.GET_SOLD_POSTS_SUCCESS:
        return {
          ...state,
          soldPosts: action.soldPosts,
        };
      case userConstants.GET_SOLD_POSTS_FAILURE:
        return {
          ...state,
        };

    default:
      return state;
  }
}
