import React, { Component } from "react";
import { connect } from "react-redux";
import { Form, Grid, Input, Message, Select } from "semantic-ui-react";
import { postActions, validatorActions } from "../../actions";
import { DateInput } from "semantic-ui-calendar-react";

class Step2 extends Component {
  schema = {
    required: ["originalQuantity", "title"],
    properties: {
      originalQuantity: {
        title: "originalQuantity",
        type: "number",
        minimum: 1,
      },

      title: {
        title: "title",
        type: "string",
        maxLength: 255,
        minLength: 20,
      },
    },
  };
  handleChange = (e, { name, value }) => {
    this.props.mainDataChanged(name, value);

    if (name === "originalQuantity")
      this.props.validateInput(this.schema, name, parseInt(value));
    if (name === "title") this.props.validateInput(this.schema, name, value);
  };

  render() {
    const conditionOptions = [
      { key: 1, value: "New and sealed", text: "New and sealed" },
      { key: 2, value: "New with open box", text: "New with open box" },
      { key: 3, value: "Used", text: "Used" },
      { key: 4, value: "Defective", text: "Defective" },
    ];

    const warrantyOptions = [
      { key: 1, value: true, text: "Yes" },
      { key: 2, value: false, text: "No" },
    ];

    const { currentStep, mainData, errors } = this.props;
    const {
      title,
      condition,
      warranty,
      expiryDate,
      originalQuantity,
    } = mainData;

    return currentStep !== 2 ? null : (
      <Grid
        centered
        columns={2}
        style={{ padding: "2em", minHeight: 500 }}
        textAlign="center"
        verticalAlign="middle"
      >
        <Grid.Column>
          <Form.Field
            control={Input}
            name="title"
            label="Title"
            placeholder="Iphone XS Max"
            value={title}
            onChange={this.handleChange}
          />

          {errors && errors.title && (
            <Message negative>
              <Message.Header>{errors.title}</Message.Header>
            </Message>
          )}
          <Form.Field
            control={Select}
            name="condition"
            label="Condition"
            placeholder="Choose condition..."
            value={condition}
            options={conditionOptions}
            onChange={this.handleChange}
          />

          <Form.Field
            control={Select}
            name="warranty"
            label="Warranty"
            placeholder="Has warranty..."
            value={warranty}
            options={warrantyOptions}
            onChange={this.handleChange}
          />

          <Form.Field disabled={!warranty}>
            <label>Warranty Expiry Date</label>
            <DateInput
              name="expiryDate"
              size="large"
              dateFormat={"Y-MM-DD"}
              placeholder="2019-01-15"
              value={expiryDate}
              onChange={this.handleChange}
            />
          </Form.Field>

          <Form.Field />
          <Form.Field>
            <label> Quantity</label>
            <Input
              control={Input}
              name="originalQuantity"
              type="number"
              min={1}
              size="large"
              placeholder="10"
              value={originalQuantity}
              onChange={this.handleChange}
            ></Input>
          </Form.Field>
          {errors && errors.originalQuantity && (
            <Message negative>
              <Message.Header>{errors.originalQuantity}</Message.Header>
            </Message>
          )}
        </Grid.Column>
      </Grid>
    );
  }
}

function mapState(state) {
  const { currentStep, mainData } = state.post;
  const { alert } = state;
  const { valid, errors } = state.validation;

  return { currentStep, mainData, alert, errors, valid };
}

const actionCreators = {
  mainDataChanged: postActions.mainDataChanged,
  validateInput: validatorActions.validateInput,
};

const connectedStep2 = connect(mapState, actionCreators)(Step2);
export { connectedStep2 as Step2 };
