import React from "react";
import { Modal, Button, Header } from "semantic-ui-react";

const CheckEmailModal = ({
    userEmail,
    showModal,
    handleCloseModal,
}) => {
    return (
        <Modal
          onClose={handleCloseModal}
          open={(!userEmail.email || !userEmail.isVerified) && showModal}
        >
          <Modal.Header>{!userEmail.email ? 'Email Not Updated' : 'Email Not Verified'}</Modal.Header>
          <Modal.Content image>
            <Modal.Description>
              <Header>{!userEmail.email ? 'Please Update Your Email To Continue...' : ''}</Header>
              <p>{!userEmail.email ? 'We will take you there!' : ''}</p>
            </Modal.Description>
          </Modal.Content>
          <Modal.Actions>
            <Button
              content="Go"
              labelPosition='right'
              icon='checkmark'
              onClick={() => handleCloseModal(true)}
              positive
            />
          </Modal.Actions> 
        </Modal>
      )
}

export default CheckEmailModal;
