import { httpService } from "./";
import { apiUrl } from "../config";

export const discountService = {
    createDiscount,
    removeDiscount,
};

const apiEndpoint = apiUrl + "/discount";

function createDiscount(body,postId) {
  return httpService.post(`${apiEndpoint}/${postId}`,body);
}
function removeDiscount(postId) {
  return httpService.put(`${apiEndpoint}/remove/${postId}`);
}
