import { orderConstants } from "../constants";

const initialState = {
  displayedStep: orderConstants.PLACE_ORDER_STEP_REVIEW,
  pickUpInfo: {
    picker: "Buyer",
    nearestServicePointChecked: false,
  },
  orderHistory: {
    ordersAsSeller: [],
    ordersAsBuyer: [],
    returnRequestsSeller: [],
    returnRequestsBuyer: [],
  },
  buyingOrders: [],
  sellingOrders:[],
  sellingPosts:[],
  orderDetailsData: {
    buyerData: {},
    items: [],
  },
  returnRequestsOrder: [],
  editedOrderItems: [],
  editItemsActive: false,
  ordersDisplayed: orderConstants.VIEW_ORDERS_AS_BUYER,
  // returnRequested:true
};

export function order(state = initialState, action) {
  switch (action.type) {
    ///////////////////////BUTTONS ACTIONS/////////////////////
    case orderConstants.CONFIRM_AUTHORIZED_PERSON_DATA_REQUEST:
      return {
        ...state,
      };

    case orderConstants.CONFIRM_AUTHORIZED_PERSON_DATA_FAILURE:
      return {
        ...state,
      };
    ////////////////////////STEPS ACTIONS//////////////////////
    case orderConstants.PLACE_ORDER_STEP_REVIEW:
      return {
        ...state,
        displayedStep: orderConstants.PLACE_ORDER_STEP_REVIEW,
      };
    ////////////////////////PLACE ORDER ACTIONS//////////////////////
    case orderConstants.PLACE_ORDER_REQUEST:
      return {
        ...state,
      };
    case orderConstants.PLACE_ORDER_SUCCESS:
      return {
        ...state,
        ordersData: action.ordersData,
        displayedStep: orderConstants.PLACE_ORDER_STEP_INFORMATION,
      };
    case orderConstants.PLACE_ORDER_FAILURE:
      return {
        ...state,
      };
    ////////////////////////EDIT PICKUP INFO ACTIONS//////////////////////
    case orderConstants.SUBMIT_BUYER_PICKUP_INFO_REQUEST:
      return {
        ...state,
      };
    case orderConstants.SUBMIT_BUYER_PICKUP_INFO_SUCCESS:
      return {
        ...state,
      };
    case orderConstants.SUBMIT_BUYER_PICKUP_INFO_FAILURE:
      return {
        ...state,
      };
    ////////////////////////VIEW ORDER ACTIONS//////////////////////
    case orderConstants.VIEW_ALL_ORDERS_REQUEST:
      return {
        ...state,
      };
    case orderConstants.VIEW_ALL_ORDERS_SUCCESS:
      return {
        ...state,
        orderHistory: action.orderHistory,
      };
    case orderConstants.VIEW_ALL_ORDERS_FAILURE:
      return {
        ...state,
      };
    case orderConstants.VIEW_ORDER_REQUEST:
      return {
        ...state,
      };
    case orderConstants.VIEW_ORDER_SUCCESS:
      return {
        ...state,
        orderDetailsData: action.orderData,
        returnRequestsOrder: action.returnRequestsOrder,
      };
    case orderConstants.VIEW_ORDER_FAILURE:
      return {
        ...state,
      };
    //buying orders
    case orderConstants.VIEW_BUYINGORDER_REQUEST:
      return {
        ...state,
      };
    case orderConstants.VIEW_BUYINGORDER_SUCCESS:
      return {
        ...state,
        buyingOrders: action.buyingOrders,
      };
    case orderConstants.VIEW_BUYINGORDER_FAILURE:
      return {
        ...state,
      };

      //selling orders
      case orderConstants.VIEW_SELLINGORDER_REQUEST:
        return {
          ...state,
        };
      case orderConstants.VIEW_SELLINGORDER_SUCCESS:
        return {
          ...state,
          sellingOrders: action.sellingOrders,
        };
      case orderConstants.VIEW_SELLINGORDER_FAILURE:
        return {
          ...state,
        };

        //posts selling
        case orderConstants.VIEW_SELLINGPOSTS_REQUEST:
          return {
            ...state,
          };
        case orderConstants.VIEW_SELLINGPOSTS_SUCCESS:
          return {
            ...state,
            sellingPosts: action.sellingPosts,
          };
          
        case orderConstants.VIEW_SELLINGPOSTS_FAILURE:
          return {
            ...state,
          };
          
    /////////////////////////VIEW ORDERS ACTIONS/////////////////////////////
    case orderConstants.VIEW_ORDERS_AS_BUYER:
      return {
        ...state,
        ordersDisplayed: orderConstants.VIEW_ORDERS_AS_BUYER,
      };
    case orderConstants.VIEW_ORDERS_AS_SELLER:
      return {
        ...state,
        ordersDisplayed: orderConstants.VIEW_ORDERS_AS_SELLER,
      };

    case orderConstants.SELLER_CHOOSE_DROP_OFF_DATA:
      return {
        ...state,
        displayedStep: orderConstants.SELLER_STEP_DROP_OFF_INFO,
        orderId: action.orderId,
      };
    case orderConstants.SELLER_STEP_DROP_OFF_INFO_SUCCESS:
      return {
        ...state,
        ordersDisplayed: orderConstants.VIEW_ORDERS_AS_SELLER,
      };

    ///////////////////////Edit ORDER /////////////////////
    case orderConstants.MAP_ORDER_ITEMS_QUANTITY:
      return {
        ...state,
        editedOrderItems: action.items,
      };
    case orderConstants.EDIT_ORDER_ITEM_QUANTITY:
      let active = false;

      action.items.map((item) => {
        if (item.item.post._id === action.postId) {
          item.quantity = action.quantity;
        }
        return item;
      });

      action.items.forEach((item) => {
        if (item.originalQuantity !== item.quantity) active = true;
      });

      return {
        ...state,
        editItemsActive: active,
        editedOrderItems: action.items,
      };
    case orderConstants.CANCEL_EDIT_ORDER_ITEMS:
      state.editedOrderItems.forEach(
        (item) => (item.quantity = item.originalQuantity)
      );
      return {
        ...state,
        editItemsActive: false,
      };
    /////////////////////// Clear State /////////////////////
    case orderConstants.CLEAR:
      return initialState;

    case orderConstants.CHOOSE_BUYNOW_REQUEST:
      return {
        ...state,
      };
    case orderConstants.CHOOSE_BUYNOW_SUCCESS:
      return {
        ...state,
        orderDetailsData: action.orderData,
      };
    case orderConstants.CHOOSE_BUYNOW_FAILURE:
      return {
        ...state,
      };
    case orderConstants.CHOOSE_AUCTION_WITH_MIN_PRICE_REQUEST:
      return {
        ...state,
      };
    case orderConstants.CHOOSE_AUCTION_WITH_MIN_PRICE_SUCCESS:
      return {
        ...state,
        orderDetailsData: action.orderData,
      };
    case orderConstants.CHOOSE_AUCTION_WITH_MIN_PRICE_FAILURE:
      return {
        ...state,
      };
    case orderConstants.CHOOSE_AUCTION_WITHOUT_MIN_PRICE_REQUEST:
      return {
        ...state,
      };
    case orderConstants.CHOOSE_AUCTION_WITHOUT_MIN_PRICE_SUCCESS:
      return {
        ...state,
        orderDetailsData: action.orderData,
      };
    case orderConstants.CHOOSE_AUCTION_WITHOUT_MIN_PRICE_FAILURE:
      return {
        ...state,
      };
    default:
      return {
        ...state,
      };
  }
}
