import React, { Component } from 'react';
import { connect } from "react-redux";
import {  Button, Grid, Header, Label } from 'semantic-ui-react';
import { returnRequestsActions, servicePointActions } from '../../actions';
import { SelectSPAndTimeSlot } from '../ServicePoint';
import { isPickUpDataValid } from ".././ServicePoint/shared";
import * as moment from "moment";

class ReturnedItemDropOffPage extends Component {
    componentDidMount= async()=> {
       const returnRequestId = this.props.match.params.id;

       await this.props.getServicePointsData();
       await this.props.viewReturnRequest(returnRequestId);
     }    
     formatDate(date) {
        return moment(date).format("YYYY-MM-DD HH:mm:ss");
      }
     state = {  }
     handleSubmitDropOffInfo=async()=>{
         const returnRequestId=this.props.match.params.id;
         const returnReqDropOffInfo={
             servicePointId : this.props.servicePointId,
             arrivalDate : this.props.arrivalDate,
             arrivalTime : this.props.arrivalTime,
         }
        await this.props.confirmReturnRequestDropOffInfo(returnRequestId,returnReqDropOffInfo);
     }
     enableConfirmPickup = () => {
        return isPickUpDataValid(this.props);
      };
    render() { 
        const {currentReturnRequest}=this.props
        return (
                <Grid>
                    <Grid.Row style={{ textAlign: "center" ,padding: '6em 4em' }}>
                        <Grid.Column>
                        <Header as="h1"color='grey' textAlign='center'>
                        Return Request Drop Off Info </Header>
                        </Grid.Column>
                    </Grid.Row>
                    {currentReturnRequest.buyerData && 
                    currentReturnRequest.buyerData.SP &&
                    currentReturnRequest.buyerData.timeSlot ?(
                        <React.Fragment>
                        <Grid.Row style={{padding: '2em 6em' }}>
                            <Grid.Column> 
                            <Label size='huge'>You Have Already Chosen The Drop Off Info</Label>
                            </Grid.Column>
                        </Grid.Row>
                        <Grid.Row style={{padding: '2em 8em' }}>
                            <Label size="large">Service Point: {currentReturnRequest.buyerData.SP.name}</Label>
                        </Grid.Row>
                        <Grid.Row style={{padding: '2em 8em' }}>
                            <Label size="large">Time Slot: {this.formatDate(currentReturnRequest.buyerData.timeSlot)}</Label>
                        </Grid.Row>
                        </React.Fragment>

                        
                    ):(
                        <React.Fragment>
                    <Grid.Row style={{padding: '0em 6em' }}>
                        <Grid.Column width={8}> 
                        <Label size='large'>Note that you must drop off the item by yourself</Label>
                        </Grid.Column>
                        <Grid.Column width={6} style={{ textAlign: "right" }}>
                        <Button color='green' onClick={this.handleSubmitDropOffInfo} disabled={!this.enableConfirmPickup()}> Submit Drop Off Info </Button>
                        </Grid.Column>
                    </Grid.Row>
                    <Grid.Row style={{padding: '2em 6em' }}>
                        <SelectSPAndTimeSlot/>
                    </Grid.Row>
                    </React.Fragment>)}
                </Grid>
          );
    }
}
 
function mapState(state) {
    const {
        personInCharge,
        authorizedDataConfirmed,
        authorizedName,
        authorizedID,
        authorisedUserId,
        servicePoints,
        servicePointId,
        arrivalDate,
        arrivalTime,
      } = state.servicePoint;
      const {currentReturnRequest} = state.returnRequests
      return {
        personInCharge,
        authorizedDataConfirmed,
        authorizedName,
        authorizedID,
        authorisedUserId,
        servicePoints,
        servicePointId,
        arrivalDate,
        arrivalTime,
        currentReturnRequest
      };    
  }
  
const actionCreators = {
    getServicePointsData: servicePointActions.getServicePointsData,
    confirmReturnRequestDropOffInfo: returnRequestsActions.confirmReturnRequestDropOffInfo,
    viewReturnRequest: returnRequestsActions.viewReturnRequest,
  };
  
  const connectedReturnedItemDropOffPage = connect(
    mapState,
    actionCreators
  )(ReturnedItemDropOffPage);
  
  export { connectedReturnedItemDropOffPage as ReturnedItemDropOffPage };
