import { registerConstants } from '../constants';

const initialState = {
    step: 1,
    registerData: {},

    // errors
    phoneError: "",
    otpError: "",
    registerError: ""
}

export function registerReducer(state = initialState, action) {
    switch (action.type) {
        case registerConstants.SET_STEP: {
            return {
                ...state,
                step: action?.payload.step
            }
        }

        case registerConstants.SET_USER_DATA: {
            return {
                ...state,
                registerData: {
                    ...state.registerData,
                    ...action.payload.data

                }
            }
        }

        case registerConstants.CHECK_PHONE_VALIDITY: {
            return {
                ...state,
                phoneError: "",
                otpError: "",
                registerError: ""
            }
        }

        case registerConstants.CHECK_PHONE_VALIDITY_FAILURE: {
            return {
                ...state,
                phoneError: action?.payload?.message
            }
        }

        case registerConstants.VERIFY_OTP: {
            return {
                ...state,
                phoneError: "",
                otpError: "",
                registerError: ""
            }
        }

        case registerConstants.VERIFY_OTP_FAILURE: {
            return {
                ...state,
                otpError: action?.payload?.message
            }
        }


        case registerConstants.REGISTER: {
            return {
                ...state,
                phoneError: "",
                otpError: "",
                registerError: ""
            }
        }

        case registerConstants.REGISTER_FAILURE: {
            return {
                ...state,
                registerError: action?.payload?.message
            }
        }
        case registerConstants.RESET_OTP_ERROR: {
            return {
                ...state,
                otpError: ""
            }
        }

        default:
            return state
    }
}
