import React, {Component, Fragment} from 'react';
import {connect} from 'react-redux';
import {Button, Card, Container, Image, Placeholder} from "semantic-ui-react";
import {Link} from "react-router-dom";
import Slider from "react-slick";

import {postActions} from "../../actions";
import {backendUrl, imageUrl} from "../../config";

import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";
import { onSale } from '../../helpers';
import DiscountCard from '../../components/Post/DiscountCard';

class PostsPage extends Component {
  async componentDidMount() {
    const {posts} = this.props.myPosts;
    if (posts.length < 1)
      await this.props.getMyPosts();
  }

  handleDeletePost = async (postId, postStatus) => {
    if (postStatus !== 'pendingReview' && postStatus !== 'declined') return;
    await this.props.deletePost(postId);
  };

  renderImages = (post) => {
    const settings = {
      dots: false, autoplay: true, autoplaySpeed: 2000, infinite: true, speed: 1000, slidesToShow: 1, slidesToScroll: 1, arrows: false, adaptiveHeight: false
    };
    return (
      <Slider {...settings}>
        {
          post.images.map(image => {
            return (
              <div key={image}>
                <Image src={`${imageUrl}/${image}`} rounded fluid/>
              </div>
            )
          })
        }
      </Slider>
    )
  };

  render() {
    const {posts, loading} = this.props.myPosts;
    return (
      <Container fluid style={{padding: '2em 10em'}}>
        <Card.Group centered itemsPerRow={3} stackable>
          {posts.map((post) => (
            <Card key={post._id}>
              {loading ? (
                <Placeholder>
                  <Placeholder.Image rectangular/>
                </Placeholder>
              ) : (this.renderImages(post))}
              <Card.Content>
                {loading ? (
                  <Placeholder>
                    <Placeholder.Header>
                      <Placeholder.Line length='very short'/>
                      <Placeholder.Line length='medium'/>
                    </Placeholder.Header>
                    <Placeholder.Paragraph>
                      <Placeholder.Line length='short'/>
                    </Placeholder.Paragraph>
                  </Placeholder>
                ) : (
                  <Fragment>
                    {
                      onSale(post.discount) ?
                      <DiscountCard post={post} />
                      :
                      <Fragment>
                        <Card.Header>{`${post.title}`}</Card.Header>
                          { 
                            post.sellingMethod.toLowerCase().includes('buynow') ?
                            <Card.Meta>{`${post.price} EGP`}</Card.Meta> : <Card.Meta> Auction Only </Card.Meta> 
                          }
                      </Fragment>
                    }
                    <Card.Meta>{post.status}</Card.Meta>
                    {post.status === 'declined' && 
                    <React.Fragment>
                      <Card.Meta>Decline Reason: </Card.Meta>
                      <Card.Meta>{post.declineReason}</Card.Meta>
                    </React.Fragment>}
                    <Card.Description>Remaining {post.availableQuantity} From {post.originalQuantity}</Card.Description>
                  </Fragment>
                )}
              </Card.Content>
              <Card.Content extra>
                <div className='ui three buttons'>
                  <Button as={Link} to={`/post/${post._id}`} disabled={loading} basic color='blue'>View</Button>
                  {(post.status === 'pendingReview' || post.status === 'declined') && <Button disabled={loading} onClick={() => this.handleDeletePost(post._id, post.status)} basic color='red'>Delete</Button>}
                </div>
              </Card.Content>
            </Card>
          ))}
        </Card.Group>
      </Container>
    );
  }
}

function mapState(state) {
  const {authentication, myPosts} = state;
  const {user} = authentication;
  return {user, myPosts};
}

const actionCreators = {
  getMyPosts: postActions.getMyPosts,
  deletePost: postActions.deletePost,
};

const connectedPostsPage = connect(mapState, actionCreators)(PostsPage);
export {connectedPostsPage as PostsPage};
