import { moneyWithdrawalConstatnt } from "../constants";

const initialState = {
  withdrawalRequests: []
};
export function moneyWithdrawal(state = initialState, action) {
  switch (action.type) {
    case moneyWithdrawalConstatnt.VIEW_ALL_REQUEST:
      return {
        ...state
      };
    case moneyWithdrawalConstatnt.VIEW_ALL_SUCCESS:
      return {
        ...state,
        withdrawalRequests: action.data.withdrawalRequests
      };
    case moneyWithdrawalConstatnt.VIEW_ALL_FAILURE:
      return {
        ...state
      };
    case moneyWithdrawalConstatnt.VIEW_ONE_REQUEST:
      return {
        ...state
      };
    case moneyWithdrawalConstatnt.VIEW_ONE_SUCCESS:
      return {
        ...state,
        withdrawalRequests: action.data.withdrawalRequest
      };
    case moneyWithdrawalConstatnt.VIEW_ONE_FAILURE:
      return {
        ...state
      };
    case moneyWithdrawalConstatnt.EDIT_ITEM_REQUEST:
      return {
        ...state
      };
    case moneyWithdrawalConstatnt.EDIT_ITEM_SUCCESS:
      return {
        ...state,
        withdrawalRequests: action.data.withdrawalRequest
      };
    case moneyWithdrawalConstatnt.EDIT_ITEM_FAILURE:
      return {
        ...state
      };
      case moneyWithdrawalConstatnt.SAVE_ITEM_REQUEST:
      return {
        ...state
      };
    case moneyWithdrawalConstatnt.SAVE_ITEM_SUCCESS:
      return {
        ...state,
        withdrawalRequests: action.data.withdrawalRequest
      };
    case moneyWithdrawalConstatnt.SAVE_ITEM_FAILURE:
      return {
        ...state
      };

    case moneyWithdrawalConstatnt.REMOVE_ITEM_REQUEST:
      return {
        ...state
      };
    case moneyWithdrawalConstatnt.AMOUNT_CHANGE:
      state.withdrawalRequests.amount = action.amount;
      return {
        ...state
      };

    case moneyWithdrawalConstatnt.TIME_SLOT_CHANGE:
      state.withdrawalRequests.timeslot = action.timeslot;
      return {
        ...state
      };
    case moneyWithdrawalConstatnt.SP_CHANGE:
      const withdrawalRequests = {...state.withdrawalRequests};
    
    if (typeof state.withdrawalRequests.SP === "string")
        withdrawalRequests.SP = action.id;
      else withdrawalRequests.SP._id = action.id;
      return {
        ...state,
        withdrawalRequests
      };
    case moneyWithdrawalConstatnt.REMOVE_ITEM_SUCCESS:
      let updatedState = {
        ...state
      };

      const filteredWithdrawal = updatedState.withdrawalRequests.map(function(item) {
        if (item._id !== action.data._id) {
          return item;
        } else {
          return action.data;
        }
      });

      updatedState.withdrawalRequests = filteredWithdrawal;
      return {
        ...state,
        withdrawalRequests:filteredWithdrawal
      };
    case moneyWithdrawalConstatnt.REMOVE_ITEM_FAILURE:
      return {
        ...state
      };
    default:
      return {
        ...state
      };
  }
}
