import React, { Component } from "react";
import { Dropdown, Menu, Image } from "semantic-ui-react";
import { Link } from "react-router-dom";
import i18n from "i18next";
import { withTranslation } from "react-i18next";
import { connect } from "react-redux";
import { userActions } from "../../actions";
import "./UserProfilePage.css";
import BuyingIcon from "../../images/02Icons01Indicators02ReversePrintsReceipt@3x.png";
import SellingsIcon from "../../images/02Icons01Indicators02ReversePrintsPost@3x.png";
import InquiriesIcon from "../../images/02Icons01Indicators02ReversePrintsChat@3x.png";
import StatementIcon from "../../images/02Icons01Indicators02ReversePrintsStatement@3x.png";
import BadgesIcon from "../../images/02Icons01Indicators02ReversePrintsBadge@3x.png";
import ReviewsIcon from "../../images/02Icons01Indicators02ReversePrintsRateFilled@2x.png";
import AuthorizedPeopleIcon from "../../images/02Icons01Indicators02ReversePrintsContact@3x.png";
import InviteFriendsIcon from "../../images/02Icons01Indicators02ReversePrintsAddPeople@2xpng.png";
import { routeConstants } from "../../constants";

class UserProfilePage extends Component {
  componentDidMount() {
    document.body.dir = i18n.dir();
  }
  render() {
    const { t } = this.props;
    return (
      <Menu vertical>
        <Dropdown
          item
          pointing="left"
          className="link item"
          as={Link}
          to={routeConstants.BUYING}
        >
          <div className="userProfileDropdownandicon">
            <div className="imageStyle">
              <Image src={BuyingIcon} />
            </div>
            <span>{t("Buyings")}</span>
          </div>
        </Dropdown>
        <Dropdown
          item
          pointing="left"
          className="link item"
          as={Link}
          to={routeConstants.SELLING}
        >
          <div className="userProfileDropdownandicon">
            <div className="imageStyle">
              <Image src={SellingsIcon} />
            </div>
            <span>{t("Sellings")}</span>
          </div>
        </Dropdown>
        <Dropdown
          item
          pointing="left"
          className="link item"
          as={Link}
          to={routeConstants.INQUIRIES}
        >
          <div className="userProfileDropdownandicon">
            <div className="imageStyle">
              <Image src={InquiriesIcon} />
            </div>
            <span>{t("Inquiries")}</span>
          </div>
        </Dropdown>
        {/* Things we might need again later */}

        {/* <Dropdown item pointing="left" className="link item">
          <div className="userProfileDropdownandicon">
            <div className="imageStyle">
              <Image src={StatementIcon} />
            </div>
            <span>{t("Statements")}</span>
          </div>
        </Dropdown>
        <Dropdown item pointing="left" className="link item">
          <div className="userProfileDropdownandicon">
            <div className="imageStyle">
              <Image src={BadgesIcon} />
            </div>
            <span>Badges</span>
          </div>
        </Dropdown> */}
        {/* <Dropdown item pointing="left" className="link item">
          <div className="userProfileDropdownandicon">
            <div className="imageStyle">
              <Image src={AuthorizedPeopleIcon} />
            </div>
            <span>Authorized People</span>
          </div>
        </Dropdown> */}
        <Dropdown
          item
          pointing="left"
          className="link item"
          as={Link}
          to={routeConstants.REVIEW}
        >
          <div className="userProfileDropdownandicon">
            <div className="imageStyle">
              <Image src={ReviewsIcon} />
            </div>
            <span>{t("Reviews")}</span>
          </div>
        </Dropdown>
        <Dropdown
          item
          pointing="left"
          className="link item"
          as={Link}
          to={routeConstants.INVITEFRIENDS}
        >
          <div className="userProfileDropdownandicon">
            <div className="imageStyle">
              <Image src={InviteFriendsIcon} />
            </div>
            <span>{t("Invite Friends")}</span>
          </div>
        </Dropdown>
      </Menu>
    );
  }
}

function mapState(state) {
  const { user } = state.authentication;
  const { userProfile, language } = state.users;
  return { user, userProfile, language };
}

const actionCreators = {
  updateUserAccountStatus: userActions.updateUserAccountStatus,
  changeLanguage: userActions.setLanguage,
};

const connectedUserProfilePage = connect(
  mapState,
  actionCreators
)(withTranslation()(UserProfilePage));

export { connectedUserProfilePage as UserProfilePage };
