import React, {Component} from 'react';
import {connect} from 'react-redux';
import {Button, Form, Grid, Header, Message, Segment} from "semantic-ui-react";
import {Link} from "react-router-dom";
import {history} from '../../helpers';
import {authActions, validatorActions, userActions} from '../../actions';

export class LoginContainer extends Component {
  state = {phone: '', password: '', submitted: false, otp: ''};
  schema = {
    "required": ["phone", "password"],
    "properties": {
      "phone": {
        "title": "Phone",
        "type": "string",
        "pattern": "^01[0-2|5]{1}[0-9]{8}$",
      }, "password": {
        "title": "Password",
        "type": "string",
        "minLength": 8,
        "maxLength": 1024
      }, "otp": {
        "title": "OTP",
        "type": "string",
        pattern: "^[0-9]{4}$",
      }
    }
  };

  handleChange = (e, {name, value}) => {
    this.props.validateInput(this.schema, name, value);
    this.setState({[name]: value});
  };

  handleSubmit = async (e) => {
    e.preventDefault();
    this.setState({submitted: true});
    const { phone, password } = this.state;
    await this.props.validateForm(this.schema, {phone, password});
    this.setState({submitted: false});
    if (!this.props.valid) return;
    await this.props.login(phone, password);
  };

  resendOTP = async (e) => {
    e.preventDefault();
    this.setState({ loading: true });
    const phone = this.state.phone ? this.state.phone : this.props.user.phone;
    await this.props.resendOTP(phone);
    this.setState({ loading: false });
  }

  handleSubmitOTP = async (e) => {
    e.preventDefault();
    if (this.props.errors.otp) return;
    this.setState({submitted: false});
    await this.props.submitOTP(this.state.otp, this.props.user.phone);
    history.push('/');
  }

  ResendAlert(alert) {
      if (alert && alert.type === 'error' && (alert.header.includes('token') || alert.header.includes('not verified') || alert.header.includes('OTP'))) {
          return (
              <Message
              error={alert.type === 'error'}
              header={alert.header}
              content={alert.content}
          >
            {alert.header}&nbsp;<Link to='#' onClick={this.resendOTP}>Send OTP</Link>&nbsp;
          </Message>

          )
      }
  }

  render() {
    const {loggingIn, errors, alert, verified } = this.props;
    const {phone, password, submitted, otp} = this.state;

    return (
      <Grid textAlign='center' style={{height: '100vh'}} verticalAlign='middle'>
        <Grid.Column style={{maxWidth: 450}}>
          <Header as='h2' color='teal' textAlign='center'> {'Login'}</Header>
         
          { alert && alert.header &&
            (this.ResendAlert(alert) ||
            <Message 
                error={alert.type === 'error'}
                success={alert.type === 'success'}
                header={alert.header}
                content={alert.content}
            >
                {/* {alert.header}
                <Button icon='redo'/> */}
            </Message>)

          }
          {verified ? 
          
          (<React.Fragment>
            <Form loading={loggingIn || submitted} size='large' onSubmit={this.handleSubmit}>
              <Segment>
                <Form.Input name='phone' size='large' icon='phone' iconPosition='left'
                            placeholder='01122334455' value={phone} onChange={this.handleChange}
                            error={errors && errors.phone}/>
  
                <Form.Input name='password' size='large' icon='lock' iconPosition='left' placeholder='Password'
                            value={password} type='password' onChange={this.handleChange}
                            error={errors && errors.password}/>
  
                <Form.Field
                  label={{ children: 'Copyright © 2020 Mazadat by Techtag LLC All rights reserved' }}
                />
                <Button color='teal' fluid>Login</Button>
              </Segment>
            </Form>
            <Message><Link to='/password/forget'> Forget Password </Link></Message>
          </React.Fragment>
          ) : (
            <React.Fragment>
              <Form size='large' onSubmit={this.handleSubmitOTP}>
                <Segment>
                  <Form.Input name='otp' size='large' icon='lock' iconPosition='left'
                              placeholder='******' value={otp} onChange={this.handleChange}
                              error={errors && errors.otp}/>
    
                  <Button color='teal' fluid>Submit OTP</Button>
                </Segment>
              </Form>
              <Button color='red' onClick={this.resendOTP}>Resend OTP</Button>
            </React.Fragment>
          )}

          {/* removed due to invitation */}
          {/* <Message>Don't have an account? <Link to='/register'>Register</Link></Message> */}
        </Grid.Column>
      </Grid>
    );
  }
}

function mapState(state) {
  const {alert} = state;
  const {loggingIn, user} = state.authentication;
  const {valid, errors} = state.validation;
  const {verified, language} = state.users
  return {verified, language, user, loggingIn, valid, errors, alert};
}

const actionCreators = {
  login: authActions.login,
  submitOTP: userActions.submitOTP,
  resendOTP: userActions.resendOTP,
  validateInput: validatorActions.validateInput,
  validateForm: validatorActions.validateForm,
};
const connectedLoginPage = connect(mapState, actionCreators)(LoginContainer);
export {connectedLoginPage as LoginPage};
