import React from 'react';
import { Card, Label } from 'semantic-ui-react';
import { calculatePrice } from '../../helpers';

const DiscountCard = ({ post }) => {
    const { price, discount, title, sellingMethod } = post;
    const { discount_value, discount_unit } = discount;
    const salePrice = calculatePrice(price, discount);
    return (
        <React.Fragment>
            {
                <Label color='red' ribbon>
                    {discount_value} {discount_unit === 'percentage' ? '%' : 'LE'} OFF
                </Label>
            }
            <br />
            <br />
            <Card.Header>{`${title}`}</Card.Header>
            { 
                sellingMethod.toLowerCase().includes('buynow') ?
                <React.Fragment>
                    <Card.Meta><strong>{`${salePrice} EGP`}</strong></Card.Meta> 
                    <Card.Meta><strike>{`${price} EGP`}</strike></Card.Meta> 
                </React.Fragment>
                : 
                    <Card.Meta> Auction Only </Card.Meta> 
            }
        </React.Fragment>
    );
}

export default DiscountCard;
