import {userConstants} from '../constants';

const initialState = {
  registering: false,
  error: '',
  submitOTPSuccessful: false,
}

export function registration(state = initialState, action) {
  switch (action.type) {
    case userConstants.REGISTER_REQUEST:
      return {...state, registering: true};
    case userConstants.REGISTER_SUCCESS:
      return {};
    case userConstants.REGISTER_FAILURE:
      return {...state, error: action.error};

    case userConstants.SUBMIT_OTP_REQUEST:
      return {...state};
    case userConstants.SUBMIT_OTP_SUCCESS:
      return { submitOTPSuccessful: true };
    case userConstants.SUBMIT_OTP_FAILURE:
      return {...state, error: action.error};  
      
    case userConstants.RESEND_OTP_REQUEST:
      return {...state};
    case userConstants.RESEND_OTP_SUCCESS:
      return {};
    case userConstants.RESEND_OTP_FAILURE:
      return {...state, error: action.error};        
    default:
      return state
  }
}
