import { avatarConstants } from '../constants';

const initialState = {
    userAvatar: '',
    avatarChanged: false,
};

export function avatar(state = initialState, action) {
    switch (action.type) {
        //////////////////// VIEW ACTIVE AVATAR //////////////////
        case avatarConstants.VIEW_ACTIVE_AVATAR_REQUEST:
          return {
            ...state,
          };
        case avatarConstants.VIEW_ACTIVE_AVATAR_SUCCESS:
          return{
            ...state,
            userAvatar: action.avatarUrl
          }
        case avatarConstants.VIEW_ACTIVE_AVATAR_FAILURE:
          return {
            ...state,
          };

        /////////////////// SUBMIT ACTIVE AVATAR /////////////////
        case avatarConstants.SUBMIT_ACTIVE_AVATAR_REQUEST:
          return {
            ...state,
          };
        case avatarConstants.SUBMIT_ACTIVE_AVATAR_SUCCESS:
          return{
            ...state,
            userAvatar: action.avatarUrl
          }
        case avatarConstants.SUBMIT_ACTIVE_AVATAR_FAILURE:
          return {
            ...state,
          };   
          
        /////////////////// CHANGE ACTIVE AVATAR /////////////////
        case avatarConstants.CHANGE_AVATAR:
          return {
            ...state,
            userAvatar: action.avatarUrl,
            avatarChanged: true,
          };
          
        /////////////////// CHANGE ACTIVE AVATAR /////////////////
        case avatarConstants.CLEAR:
          return {
            ...state,
            userAvatar: '',
            avatarChanged: false,
          };          

        ////////////////////// DEFAULT ////////////////////////
        default:
          return {
            ...state,
          };
    }
}
