import { categoryConstants } from "../constants";
  
const initialState = {
    categories: [],
    favoriteCategories: [],
    categoryDetails: {},
};

export function categories(state = initialState, action) {
    switch (action.type) {
        ////////////////GET ALL CATEGORIES//////////////////
        case categoryConstants.GET_ALL_CATEGORIES_REQUEST:
            return { ...state };
        case categoryConstants.GET_ALL_CATEGORIES_SUCCESS: {
            return {
                ...state,
                categories: action.categories,
            }
        }
        case categoryConstants.GET_ALL_CATEGORIES_FAILURE:
            return { ...state };

        ///////////////GET FAVORITE CATEGORIES////////////////
        case categoryConstants.GET_FAVORITE_CATEGORIES_REQUEST:
            return { ...state };
        case categoryConstants.GET_FAVORITE_CATEGORIES_SUCCESS: {
            return {
                ...state,
                favoriteCategories: action.favoriteCategories,
            };
        }
        case categoryConstants.GET_FAVORITE_CATEGORIES_FAILURE:
            return { ...state };   
            
        /////////////////UNFAVORITE CATEGORIES//////////////////
        case categoryConstants.UNFAVORITE_CATEGORY_REQUEST:
            return { ...state };
        case categoryConstants.UNFAVORITE_CATEGORY_SUCCESS: {
            return {
                ...state,
                favoriteCategories: action.favoriteCategories,
            };
        }
        case categoryConstants.UNFAVORITE_CATEGORY_FAILURE:
            return { ...state };  
            
        //////////////////FAVORITE CATEGORIES///////////////////
        case categoryConstants.FAVORITE_CATEGORY_REQUEST:
            return { ...state };
        case categoryConstants.FAVORITE_CATEGORY_SUCCESS: {
            return {
                ...state,
                favoriteCategories: action.favoriteCategories,
            };
        }
        case categoryConstants.FAVORITE_CATEGORY_FAILURE:
            return { ...state };  
            
        //////////////////CATEGORY DETAILS///////////////////
        case categoryConstants.GET_CATEGORY_DETAILS_REQUEST:
            return { ...state };
        case categoryConstants.GET_CATEGORY_DETAILS_SUCCESS: {
            return {
                ...state,
                categoryDetails: action.data,
            };
        }
        case categoryConstants.GET_CATEGORY_DETAILS_FAILURE:
            return { ...state };  

        default:
            return state;
    }
}
