import React, { Component } from "react";
import { connect } from "react-redux";
import { Grid, Checkbox } from "semantic-ui-react";
import { searchActions } from "../../actions";

class Filter extends Component {
  state = { values: [] };
  async componentDidMount() {
     
    this.setState({ values: this.props.values });
  }

  filterChanged = (e, { label, checked }) => {
    this.props.filterChanged(this.props.name, label, checked);
  };

  render() {
    const { name } = this.props;

    const { values } = this.state;
    
    return (
      <Grid padded>
        <Grid.Row>
          <strong> {name}</strong>
        </Grid.Row>
        {values.map((value) => (
          <Grid.Row key={name + value}>
            <Checkbox label={value} onChange={this.filterChanged} />
          </Grid.Row>
        ))}
      </Grid>
    );
  }
}

function mapState(state) {
  return { ...state };
}

const actionCreator = {
  filterChanged: searchActions.filterChanged,
};

const connectedFilter = connect(mapState, actionCreator)(Filter);

export { connectedFilter as Filter };
