import React, { Component } from "react";
import { connect } from "react-redux";
import { Grid, Label, Button, Icon, Popup } from "semantic-ui-react";
import { Filter } from "./Filter";

class Filters extends Component {
  render() {
    const { filters } = this.props;
    return (
      <Grid>
        <Grid.Row>
          <Label size='big'>Filters</Label>
          <Popup
            trigger={
              <Button
                className='ui button'
                icon
                onClick={(e) => {
                  window.location.reload();
                }}
                attached='right'
              >
                <Icon name='refresh' />
              </Button>
            }
            content='Reset All Filters'
            position='bottom center'
          />
        </Grid.Row>
        {filters.map(function (filter) {
          return (
            <Grid.Row key={filter.name} style={{ padding: "2px 30px" }}>
              <Filter name={filter.name} values={filter.values} />
            </Grid.Row>
          );
        })}
      </Grid>
    );
  }
}

function mapState(state) {
  return { ...state };
}

const actionCreators = {};

const connectedFilters = connect(mapState, actionCreators)(Filters);

export { connectedFilters as Filters };
