import React, { Component } from "react";
import { connect } from "react-redux";
import { Icon, Step, Grid, GridColumn } from "semantic-ui-react";
import { ConfirmOrderPage } from "./ConfirmOrderPage";
import { OrderInfoPage } from "./OrderInfo";
import { orderActions } from "../../actions";
import { orderConstants } from "../../constants";

class CheckoutPage extends Component {
  
  constructor(props) {
    super(props);
    this.props.resetDisplayedStep();
  }  
  
  render() {
    return (
      <Grid padded="horizontally">
        <Grid.Row centered>
          <Step.Group>
            <Step
              completed={
                this.props.displayedStep !==
                orderConstants.PLACE_ORDER_STEP_REVIEW
              }
              disabled={ this.props.displayedStep !== orderConstants.PLACE_ORDER_STEP_REVIEW }
            >
              <Icon name="handshake outline" />
              <Step.Content>
                <Step.Title>Confirm Order</Step.Title>
                <Step.Description>
                  Please Review and Place Order
                </Step.Description>
              </Step.Content>
            </Step>

            <Step
              disabled={ this.props.displayedStep === orderConstants.PLACE_ORDER_STEP_REVIEW }
            >
              <Icon name="info" />
              <Step.Content>
                <Step.Title>Order Placed</Step.Title>
              </Step.Content>
            </Step>
          </Step.Group>
        </Grid.Row>
        <Grid.Row>
          <GridColumn width={4}></GridColumn>
          <GridColumn width={6}>
            {this.props.displayedStep === 
            orderConstants.PLACE_ORDER_STEP_REVIEW ? (
              <ConfirmOrderPage />
            ) : (
              <OrderInfoPage />
            )}
          </GridColumn>
        </Grid.Row>
      </Grid>
    );
  }
}

function mapState(state) {
  const { user } = state.authentication;
  const { shoppingCartData } = state.shoppingCart;
  const { displayedStep, orderData } = state.order;
  return {
    user,
    shoppingCartData,
    displayedStep,
    orderData,
  };
}

const actionCreators = {
  resetDisplayedStep: orderActions.resetDisplayedStep,
};

const connectedCheckoutPage = connect(mapState, actionCreators)(CheckoutPage);

export { connectedCheckoutPage as CheckoutPage };
