import React, { Component } from "react";
import {
  Segment,
  Container,
  Form,
  Button,
  Image,
  Dropdown,
} from "semantic-ui-react";
import { connect } from "react-redux";
import {
  validatorActions,
  contactUsActions,
  keepInTouchAction,
} from "../../actions";
import { withTranslation } from "react-i18next";
import UserFormatInfo from "../../images/info@3x.png";
import "./InvitationRequest.css";
import InvitationPopup from "./InvitationPopup";
import { Link } from "react-router-dom";

const language = localStorage.getItem("i18nextLng");

class InvitationForm extends Component {
  updateOptions(lang) {
    return lang === "en"
      ? [
          {
            key: "p",
            text: "Using phone number",
            value: "Using phone number",
          },
          {
            key: "e",
            text: "Using email address",
            value: "Using email address",
          },
        ]
      : [
          {
            key: "p",
            text: "تسجيل عبر رقم الهاتف",
            value: "Using phone number",
          },
          {
            key: "e",
            text: "تسجيل عبر البريد الالكتروني",
            value: "Using email address",
          },
        ];
  }
  schema = {
    required: ["name"],
    properties: {
      name: {
        title: "name",
        type: "string",
        pattern:
          "^(?=^.{3,30}$)(?!(.*[0-9\u0660-\u0669].*){6})^[a-z\u0621-\u064A]([.-_]?[a-z\u0621-\u064A0-9\u0660-\u0669]+)*$",
      },
      email: {
        title: "email",
        type: "string",
        format: "email",
        pattern:
          "^[-_./0-9=?A-Z^_a-z`{|}~](.?[-_./0-9=?A-Z^_a-z`{|}~])*@[a-zA-Z0-9](-*.?[a-zA-Z0-9])*.[a-zA-Z](-?[a-zA-Z0-9])+$",
      },
      phone: {
        title: "phone",
        type: "string",
        pattern: "^01[0-2|5]{1}[0-9]{8}$",
      },
    },
  };
  state = {
    notInvitedForm: {
      name: "",
      email: "",
      phone: "",
    },
    formErrors: { email: "", phone: "" },
    submittedValid: false,
    noSubmission: true,
    selected: "Using phone number",
    active: false,
    showUsernameMessage: false,
  };

  handleSelectOption = (e, { value }) => {
    this.setState({ ...this.state, selected: value });
  };
  handleChange = (e, { name, value }) => {
    this.props.validateInput(this.schema, name, value);
    const notInvitedForm = { ...this.state.notInvitedForm };
    notInvitedForm[name] = value;
    this.setState({ ...this.state, notInvitedForm });
  };
  errorValidation = (name) => {
    return this.props.errors && this.props.errors[name];
  };

  componentDidUpdate = (prevProps) => {
    if (
      prevProps.keepInTouchState !== this.props.keepInTouchState &&
      this.props.keepInTouchState
    ) {
    }
  };

  handleSubmit = async (e) => {
    let obj;
    this.state.selected === "Using email address" ||
    this.state.selected === "تسجيل عبر البريد الالكتروني"
      ? (obj = {
          email: this.state.notInvitedForm.email,
          name: this.state.notInvitedForm.name,
        })
      : (obj = {
          phone: this.state.notInvitedForm.phone,
          name: this.state.notInvitedForm.name,
        });

    await this.props.validateForm(this.schema, obj);
    if (!this.props.valid) return;
    this.props.keepInTouch(obj);
  };
  showUserNameMessage = () => {
    this.setState({ showUsernameMessage: !this.state.showUsernameMessage });
  };
  render() {
    const language = localStorage.getItem("i18nextLng");
    const { t } = this.props;
    const { errors } = this.props;
    const { selected, showUsernameMessage } = this.state;
    const options = this.updateOptions(language);

    return (
      <div>
        <Container>
          <div className="startingLineInvitationForm"></div>
          <Segment className={`notInvitedForm ${language}`}>
            <div style={{ padding: "20px", margin: "auto" }}>
              <div className={`notInvitedStyle ${language}`}>
                <h3>{t("notInvited:notInvitedFormTitle")}</h3>
                <span>{t("landingPage:requiredFields")}</span>
                <Form>
                  <Form.Field>
                    <div className="notInvitedFormStyle">
                      <label>{t("notInvited:formRegistrationMethod")}</label>
                      <div className="circle"></div>
                    </div>
                    <Dropdown
                      fluid
                      selection
                      defaultValue={selected}
                      options={options}
                      onChange={this.handleSelectOption}
                      className="dropdownOptions"
                    />
                  </Form.Field>

                  {selected === "Using email address" ||
                  selected === "تسجيل عبر البريد الالكتروني" ? (
                    <Form.Field>
                      <div className="notInvitedFormStyle">
                        <div className="notInvitedformInputTitle">
                          <label>{t("landingPage:formEmail")}</label>
                          {this.errorValidation("email") && (
                            <span>{t("landingPage:wrongEmail")}</span>
                          )}
                        </div>
                        <div className="circle"></div>
                      </div>
                      <Form.Input
                        required
                        name="email"
                        value={this.state.email}
                        onChange={this.handleChange}
                        placeholder={t("landingPage:emailPlaceHolder")}
                      ></Form.Input>
                    </Form.Field>
                  ) : (
                    <div>
                      <div className="notInvitedFormStyle">
                        <div className="notInvitedformInputTitle">
                          <label>{t("notInvited:notInvitedPhoneNumber")}</label>
                          {this.errorValidation("phone") && (
                            <span>{t("landingPage:wrongMobile")}</span>
                          )}
                        </div>
                        <div className="circle"></div>
                      </div>
                      <Form.Input
                        required
                        name="phone"
                        value={this.state.phone}
                        placeholder="01XXXXXXXXX"
                        onChange={this.handleChange}
                        maxLength="11"
                      />
                    </div>
                  )}
                  <Form.Field>
                    <div className="notInvitedFormStyle">
                      <div className="notInvitedformInputTitle">
                        <label>{t("notInvited:notInvitedName")}</label>
                        {this.errorValidation("name") && (
                          <span>{t("landingPage:senderNameIsRequired")}</span>
                        )}
                      </div>
                      <div className="circle"></div>
                    </div>
                    <Form.Input
                      required
                      name="name"
                      value={this.state.notInvitedForm.name}
                      onChange={this.handleChange}
                      placeholder={t("landingPage:formInputName")}
                      maxLength="30"
                    ></Form.Input>
                  </Form.Field>

                  {Object.keys(errors).length > 0 && (
                    <span>{t("landingPage:errorMessage")}</span>
                  )}
                  <div className={`usernameFormats ${language}`}>
                    <Image src={UserFormatInfo} />
                    <Link onClick={this.showUserNameMessage}>
                      <p> {t("notInvited:usernameFormats")}</p>
                    </Link>
                  </div>
                  {showUsernameMessage && (
                    <div className={`usernameMessage  ${language}`}>
                      {t("notInvited:invalidUsernameErrorMessage")}
                    </div>
                  )}

                  <div className={`notInvitedFormMessage ${language}`}>
                    {t("notInvited:notInvitedFormMessage")}
                  </div>
                  <InvitationPopup
                    submit={this.handleSubmit}
                    name={this.state.notInvitedForm.name}
                    phone={this.state.notInvitedForm.phone}
                    email={this.state.notInvitedForm.email}
                    valid={this.props.valid}
                    keepInTouchState={this.props.keepInTouchState}
                    error={this.props.keepInTouchState}
                  />
                </Form>
                <Link to="/welcome">
                  <Button basic className="backbutton">
                    <span className="backbuttonSpan">
                      {t("notInvited:notInvitedBackButton")}
                    </span>
                  </Button>
                </Link>
              </div>
            </div>
          </Segment>
        </Container>
      </div>
    );
  }
}

function mapState(state) {
  const { errors, valid } = state.validation;
  const { alert } = state;
  return {
    errors,
    valid,
    alert,
