import {auctionConstants} from '../constants';

const initialState = {
    lastBid: 'No bids'
  };


  export function auction(state = initialState, action) {
    switch (action.type) {
      case auctionConstants.JOIN_AUCTION_SUCCESS:
        return {
          ...state
        };
      case auctionConstants.AUCTION_INFO_SUCCESS:          
        return {
          ...state,
          lastBid:action.lastBid
        }
      default:
            return {
              ...state
            };
    }}
