import React, { Component } from "react";
import { connect } from "react-redux";
import { Grid, Label, Loader } from "semantic-ui-react";
import { OrderCard } from "./OrderCard";

class OrdersAsBuyer extends Component {
  render() {
    const { ordersAsBuyer, returnRequestsBuyer } = this.props.orderHistory;
    const{loaded} =this.props.loaded
    
    return (
      <Grid style={{ padding: "1em 2em" }}>
        <Grid.Row>
          <Label size="big">Placed orders :</Label>
        </Grid.Row>
        {ordersAsBuyer && ordersAsBuyer.length > 0 ? (
         (!loaded ?( ordersAsBuyer.map((orderData) => {
          return (
            <OrderCard 
              key={orderData._id} 
              orderData={orderData} 
              returnRequests={returnRequestsBuyer && returnRequestsBuyer.find(request => request.orderId === orderData._id)}
            />
          );
        })):( <Loader active inline='centered' />))
        ) : (
          <Label>You have not placed orders yet </Label>
        )}
      </Grid>
    );
  }
}

function mapState(state) {
  const { orderHistory } = state.order;
  const {loaded}=state.returnRequests;
  
  return { orderHistory,loaded, };
}

const actionCreators = {};

const connectedOrdersAsBuyer = connect(mapState, actionCreators)(OrdersAsBuyer);

export { connectedOrdersAsBuyer as OrdersAsBuyer };
