import React, { Component } from "react";
import { connect } from "react-redux";
import {  Card, Tab, List } from "semantic-ui-react";
import { userActions } from "../../actions";
import { Link } from "react-router-dom";

class AuctionList extends Component {
  componentDidMount() {
    this.props.viewLostAuction();
    this.props.viewRunningAuction();
    this.props.viewWonAuction();
  }
  render() {
    const { won, lost, running } = this.props.auctionList;

    const panes = [
      {
        menuItem: "lost",
        render: () => (
          <Tab.Pane attached={false}>
            <List>
              <List.Item>
                {lost &&
                  lost.map((auction) => {
                    return (
                      <Card fluid key={auction._id}>
                        <Card.Content>
                          <Card.Header>{auction.post.title}</Card.Header>
                          <Card.Meta>
                            <strong>Status : </strong>
                            {auction.status}
                          </Card.Meta>
                          <Link
                            floated="right"
                            to={`/bid/history/${auction._id}`}
                          >
                            <h4>Bidding History</h4>
                          </Link>
                          <Link floated="left" to={`/post/${auction.post._id}`}>
                            <h4>Post</h4>
                          </Link>
                        </Card.Content>
                      </Card>
                    );
                  })}
              </List.Item>
            </List>
          </Tab.Pane>
        ),
      },
      {
        menuItem: "running",
        render: () => (
          <Tab.Pane attached={false}>
            <List>
              <List.Item>
                {running &&
                  running.map((auction) => {
                    return (
                      <Card fluid key={auction._id}>
                        <Card.Content>
                          <Card.Header>{auction.post.title}</Card.Header>
                          <Card.Meta>
                            <strong>Status : </strong>
                            {auction.status}
                          </Card.Meta>
                          <Link to={`/bid/history/${auction._id}`}>
                            <h4>Bidding History</h4>
                          </Link>
                          <Link to={`/post/${auction.post._id}`}>
                            <h4>Post</h4>
                          </Link>
                        </Card.Content>
                      </Card>
                    );
                  })}
              </List.Item>
            </List>
          </Tab.Pane>
        ),
      },
      {
        menuItem: "won",
        render: () => (
          <Tab.Pane attached={false}>
            <List>
              <List.Item>
                {won &&
                  won.map((auction) => {
                    return (
                      <Card fluid key={auction._id}>
                        <Card.Content>
                          <Card.Header>{auction.post.title}</Card.Header>
                          <Card.Meta>
                            <strong>Status : </strong>
                            {auction.status}
                          </Card.Meta>
                          <Link to={`/bid/history/${auction._id}`}>
                            <h4>Bidding History</h4>
                          </Link>
                          <Link to={`/post/${auction.post._id}`}>
                            <h4>Post</h4>
                          </Link>
                        </Card.Content>
                      </Card>
                    );
                  })}
              </List.Item>
            </List>
          </Tab.Pane>
        ),
      },
    ];
    return (
      <Tab
        style={{ margin: 100 }}
        menu={{
          secondary: true,
          pointing: true,
          fluid: true,
          vertical: false,
        }}
        panes={panes}
      />
    );
  }
}

function mapState(state) {
  const { user } = state.authentication;
  const { userProfile, auctionList } = state.users;
  return {
    userProfile,
    auctionList,
    user,
  };
}
const actionCreators = {
  viewRunningAuction: userActions.viewRunningAuction,
  viewWonAuction: userActions.viewWonAuction,
  viewLostAuction: userActions.viewLostAuction,
};

const connectedAuctionList = connect(mapState, actionCreators)(AuctionList);

export { connectedAuctionList as AuctionList };
